import React, { useState } from 'react'

function UserRegistration() {

    const [user, setUser] = useState({
      name: "Litha",
      model: "Mustang",
      year: "1964",
      color: "red"
    });

    const updateColor = () => {
      setUser(previousState => {
        return { ...previousState, color: "blue" }
      });
    }

    return (
      <>
        <h1>My {user.name}</h1>
        <p>
          It is a {user.color} {user.model} from {user.year}.
        </p>
        <button
          type="button"
          onClick={updateColor}
        >Blue</button>
      </>
    )
}

export default UserRegistration;
